import 'package:ainsighter/data/repository/auth_repo.dart';
import 'package:ainsighter/data/repository/category_repo.dart';
import 'package:ainsighter/data/repository/classified_repo.dart';
import 'package:ainsighter/data/repository/notification_repo.dart';
import 'package:ainsighter/data/repository/onboarding_repo.dart';
import 'package:ainsighter/data/repository/profile_repo.dart';
import 'package:ainsighter/data/repository/splash_repo.dart';
import 'package:ainsighter/provider/address_provider.dart';
import 'package:ainsighter/provider/auth_provider.dart';
import 'package:ainsighter/provider/category_provider.dart';
import 'package:ainsighter/provider/classified_provider.dart';
import 'package:ainsighter/provider/localization_provider.dart';
import 'package:ainsighter/provider/notification_provider.dart';
import 'package:ainsighter/provider/onboarding_provider.dart';
import 'package:ainsighter/provider/profile_provider.dart';
import 'package:ainsighter/provider/splash_provider.dart';
import 'package:ainsighter/provider/theme_provider.dart';
import 'package:ainsighter/utill/app_constants.dart';
import 'package:dio/dio.dart';
import 'package:get_it/get_it.dart';
import 'package:shared_preferences/shared_preferences.dart';

import 'data/datasource/remote/dio/dio_client.dart';
import 'data/datasource/remote/dio/logging_interceptor.dart';
import 'data/repository/address_repo.dart';

final sl = GetIt.instance;

Future<void> init() async {
  // Core
  sl.registerLazySingleton(() => DioClient(AppConstants.BASE_URL, sl(),
      loggingInterceptor: sl(), sharedPreferences: sl()));

  // Repository
  sl.registerLazySingleton(
      () => SplashRepo(sharedPreferences: sl(), dioClient: sl()));
  sl.registerLazySingleton(() => OnBoardingRepo(dioClient: sl()));
  sl.registerLazySingleton(() => CategoryRepo(dioClient: sl()));
  sl.registerLazySingleton(
      () => AuthRepo(dioClient: sl(), sharedPreferences: sl()));
  sl.registerLazySingleton(
      () => ProfileRepo(dioClient: sl(), sharedPreferences: sl()));
  sl.registerLazySingleton(() => NotificationRepo(dioClient: sl()));
  sl.registerLazySingleton(() => ClassifiedRepo(dioClient: sl()));
  sl.registerLazySingleton(() => AddressRepo(dioClient: sl()));

  // Provider
  sl.registerFactory(() => ThemeProvider(sharedPreferences: sl()));
  sl.registerFactory(() => LocalizationProvider(sharedPreferences: sl()));
  sl.registerFactory(() => SplashProvider(splashRepo: sl()));

  sl.registerFactory(() => OnBoardingProvider(onboardingRepo: sl()));
  sl.registerFactory(() => CategoryProvider(categoryRepo: sl()));
  sl.registerFactory(() => AuthProvider(authRepo: sl()));
  sl.registerFactory(() => ProfileProvider(profileRepo: sl()));
  sl.registerFactory(() => ClassifiedProvider(classifiedRepo: sl()));
  sl.registerFactory(() => NotificationProvider(notificationRepo: sl()));
  sl.registerFactory(() => AddressProvider(addressRepo: sl()));

  // External
  final sharedPreferences = await SharedPreferences.getInstance();
  sl.registerLazySingleton(() => sharedPreferences);
  sl.registerLazySingleton(() => Dio());
  sl.registerLazySingleton(() => LoggingInterceptor());
}
