import 'package:ainsighter/view/screens/dashboard/dashboard_screen.dart';
import 'package:ainsighter/view/screens/home/home_screen.dart';
import 'package:ainsighter/view/screens/user/account.dart';
import 'package:flutter/material.dart';
import 'package:font_awesome_flutter/font_awesome_flutter.dart';

class CustomBottomNav extends StatefulWidget {
  CustomBottomNav({this.message});

  final String message;

  @override
  _CustomBottomNavState createState() => _CustomBottomNavState();
}

class _CustomBottomNavState extends State<CustomBottomNav> {
  int _currentIndex = 0;

  @override
  void initState() {
    super.initState();
    if (widget.message != null) {
      WidgetsBinding.instance.addPostFrameCallback((_) =>
          ScaffoldMessenger.of(context)
              .showSnackBar(SnackBar(content: Text(widget.message))));
    }
  }

  final _pages = [
    PostScreen(),
    DashboardScreen(),
    AccountScreen(),
  ];
  @override
  Widget build(BuildContext context) {
    return SafeArea(
      child: Scaffold(
          extendBody: false,
          backgroundColor: Colors.white,
          bottomNavigationBar: SizedBox(
            height: 95,
            child: BottomNavigationBar(
              backgroundColor: Colors.transparent,
              onTap: (index) {
                setState(() {
                  _currentIndex = index;
                  print(_currentIndex);
                });
              },
              currentIndex: _currentIndex,
              elevation: 0,
              selectedIconTheme:
                  IconThemeData(color: Theme.of(context).primaryColor),
              selectedLabelStyle: TextStyle(
                fontSize: 12,
                color: Colors.black54,
              ),
              unselectedLabelStyle: TextStyle(
                fontSize: 12,
                color: Colors.black54,
              ),
              iconSize: 28,
              selectedItemColor: Colors.black54,
              items: <BottomNavigationBarItem>[
                BottomNavigationBarItem(
                  label: 'Ä°lanlar',
                  icon: Container(
                    child: FaIcon(
                      FontAwesomeIcons.search,
                    ),
                    margin: EdgeInsets.only(bottom: 3),
                  ),
                ),
                BottomNavigationBarItem(
                  label: 'Raporlar',
                  icon: Container(
                    child: FaIcon(
                      FontAwesomeIcons.chartLine,
                    ),
                    margin: EdgeInsets.only(bottom: 3),
                  ),
                ),
                BottomNavigationBarItem(
                  label: 'Hesap',
                  icon: Container(
                    child: FaIcon(
                      FontAwesomeIcons.userTie,
                    ),
                    margin: EdgeInsets.only(bottom: 3),
                  ),
                )
              ],
            ),
          ),
          body: _pages[_currentIndex]),
    );
  }
}
