import 'package:ainsighter/data/model/response/affiliate_link_model.dart';
import 'package:ainsighter/data/model/response/classified_model.dart';
import 'package:ainsighter/utill/app_constants.dart';
import 'package:ainsighter/utill/config.dart';
import 'package:ainsighter/view/base/lazy_image.dart';
import 'package:ainsighter/view/screens/classified/classified_detail_screen.dart';
import 'package:flutter/material.dart';
import 'package:flutter_svg/flutter_svg.dart';

class ClassifiedCard extends StatelessWidget {
  ClassifiedModel classified;

  ClassifiedCard({this.classified});

  @override
  Widget build(BuildContext context) {
    return Material(
      borderRadius: BorderRadius.circular(10),
      color: Colors.white,
      child: InkWell(
        borderRadius: const BorderRadius.all(Radius.circular(10)),
        splashColor: Config.PrimaryColor.withOpacity(.2),
        onTap: () {
          //if (classified.classifiedStatus == 0) {
          //  return;
          //}
          Navigator.of(context).push(MaterialPageRoute(
              builder: (_) => ClassifiedDetailScreen(
                    classifield: classified,
                  )));
        },
        child: Container(
          padding: EdgeInsets.all(10),
          decoration: BoxDecoration(
              // boxShadow: <BoxShadow>[
              //   BoxShadow(
              //       color: Colors.grey.shade200, spreadRadius: 1, blurRadius: 15)
              // ],
              ),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.center,
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: [
              Stack(
                alignment: Alignment.topRight,
                children: [
                  ClipRRect(
                      borderRadius: BorderRadius.circular(3),
                      child: LazyImage(
                        height: 140,
                        fit: BoxFit.fitHeight,
                        url:
                            '${AppConstants.BASE_URL}/storage/app/public/classifieds/${classified.images.isNotEmpty ? classified.images.first : ''}',
                      )),
                  Visibility(
                      visible: classified.classifiedStatus == 0,
                      child: Container(
                        margin: EdgeInsets.all(4),
                        padding:
                            EdgeInsets.symmetric(horizontal: 7, vertical: 4),
                        decoration: BoxDecoration(
                            color: Colors.white.withOpacity(.7),
                            borderRadius: BorderRadius.circular(3)),
                        child: Text(
                          'Onay Bekliyor',
                          style: TextStyle(color: Colors.black, fontSize: 12),
                        ),
                      ))
                ],
              ),
              Container(
                child: Text(
                  classified.title,
                  maxLines: 2,
                  overflow: TextOverflow.ellipsis,
                  style: TextStyle(
                    fontWeight: FontWeight.w500,
                    color: Config.PrimaryColor,
                    fontSize: 11,
                  ),
                ),
                decoration: BoxDecoration(
                    color: Config.PrimaryColor.withOpacity(.15),
                    borderRadius: BorderRadius.circular(3.0)),
                padding: EdgeInsets.symmetric(horizontal: 6, vertical: 4),
              ),

              // ProgressLine(),
              Column(
                children: [
                  Row(
                    mainAxisAlignment: MainAxisAlignment.spaceBetween,
                    children: [
                      Text(
                        "Tıklama",
                        style: Theme.of(context)
                            .textTheme
                            .caption
                            .copyWith(color: Colors.grey.shade600),
                      ),
                      Text(
                        "${classified.visitCount.toString()}",
                        style: Theme.of(context)
                            .textTheme
                            .caption
                            .copyWith(color: Config.PrimaryColor),
                      ),
                    ],
                  ),
                  Divider(height: 10),
                  Row(
                    mainAxisAlignment: MainAxisAlignment.spaceBetween,
                    children: [
                      Text(
                        "Satış",
                        style: Theme.of(context)
                            .textTheme
                            .caption
                            .copyWith(color: Colors.grey.shade600),
                      ),
                      Text(
                        'Yapılmadı',
                        style: Theme.of(context)
                            .textTheme
                            .caption
                            .copyWith(color: Config.PrimaryColor),
                      ),
                    ],
                  ),
                ],
              )
            ],
          ),
        ),
      ),
    );
  }

  double defaultPadding = 10;
}

class ProgressLine extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Stack(
      children: [
        Container(
          width: double.infinity,
          height: 5,
          decoration: BoxDecoration(
            color: Colors.red.withOpacity(0.1),
            borderRadius: BorderRadius.all(Radius.circular(10)),
          ),
        ),
        LayoutBuilder(
          builder: (context, constraints) => Container(
            width: constraints.maxWidth * (50 / 100),
            height: 5,
            decoration: BoxDecoration(
              color: Colors.red,
              borderRadius: BorderRadius.all(Radius.circular(10)),
            ),
          ),
        ),
      ],
    );
  }
}
