import 'package:ainsighter/provider/auth_provider.dart';
import 'package:ainsighter/view/base/custom_app_bar.dart';
import 'package:flutter/material.dart';
import 'package:provider/provider.dart';

import 'components/header.dart';

import 'components/my_fields.dart';
import 'components/recent_files.dart';
import 'components/storage_details.dart';

class DashboardScreen extends StatelessWidget {
  double defaultPadding = 10.0;

  @override
  Widget build(BuildContext context) {
    return SafeArea(
      child: Scaffold(
          extendBodyBehindAppBar: true,
          body: CustomScrollView(
            slivers: [
              SliverPersistentHeader(
                pinned: true,
                delegate: const MyHeaderDelegate(),
              ),
              SliverPadding(
                  padding: EdgeInsets.symmetric(horizontal: 10, vertical: 20),
                  sliver: SliverToBoxAdapter(child: renderBody(context)))
            ],
          )),
    );
  }

  Widget renderBody(BuildContext context) {
    if (Provider.of<AuthProvider>(context, listen: false).isLoggedIn())
      return AffiliateLinks();
    else
      return SizedBox(
          height: 500,
          child: Padding(
            padding: EdgeInsets.symmetric(horizontal: 20),
            child: Column(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                Icon(
                  Icons.lock,
                  size: 50,
                  color: Colors.grey.shade600,
                ),
                SizedBox(
                  height: 20,
                ),
                RichText(
                    textAlign: TextAlign.center,
                    text: TextSpan(
                      children: <TextSpan>[
                        TextSpan(
                          text:
                              'Raporları görebilmeniz için üye girişi yapmanız gerekmektedir.\n\n',
                          style: TextStyle(
                            color: Colors.grey.shade500,
                            fontSize: 20,
                            height: 1.6,
                          ),
                        ),
                      ],
                    )),
              ],
            ),
          ));
  }
}

class MyHeaderDelegate extends SliverPersistentHeaderDelegate {
  const MyHeaderDelegate();

  @override
  Widget build(
    BuildContext context,
    double shrinkOffset,
    bool overlapsContent,
  ) {
    final progress = shrinkOffset / maxExtent;

    return Material(
      child: Stack(
        fit: StackFit.expand,
        children: [
          AnimatedOpacity(
            duration: const Duration(milliseconds: 150),
            opacity: progress,
            child: ColoredBox(
              color: Theme.of(context).primaryColor,
            ),
          ),
          AnimatedOpacity(
            duration: const Duration(milliseconds: 150),
            opacity: 1 - progress,
            child: CustomPaint(
              painter: CustomToolbarShape(
                showBar: false,
              ),
            ),
          ),
          AnimatedContainer(
            duration: const Duration(milliseconds: 100),
            padding: EdgeInsets.lerp(
              EdgeInsets.symmetric(horizontal: 16, vertical: 8),
              EdgeInsets.only(bottom: 0),
              progress,
            ),
            alignment: Alignment.lerp(
              Alignment.bottomLeft,
              Alignment.center,
              progress,
            ),
            child: Text(
              'Raporlar',
              style: TextStyle.lerp(
                Theme.of(context)
                    .textTheme
                    .headline5
                    .copyWith(color: Colors.white),
                Theme.of(context)
                    .textTheme
                    .headline6
                    .copyWith(color: Colors.white),
                progress,
              ),
            ),
          ),
          AppBar(
            toolbarHeight: 70,
            backgroundColor: Colors.transparent,
            // leading: BackButton(),
            elevation: 0,
          ),
        ],
      ),
    );
  }

  @override
  double get maxExtent => 150;

  @override
  double get minExtent => 70;

  @override
  bool shouldRebuild(covariant SliverPersistentHeaderDelegate oldDelegate) =>
      true;
}
