class MyHeaderDelegate extends SliverPersistentHeaderDelegate {
  const MyHeaderDelegate();

  @override
  Widget build(
    BuildContext context,
    double shrinkOffset,
    bool overlapsContent,
  ) {
    final progress = shrinkOffset / maxExtent;

    return Material(
      child: Stack(
        fit: StackFit.expand,
        children: [
          AnimatedOpacity(
            duration: const Duration(milliseconds: 150),
            opacity: progress,
            child: ColoredBox(
              color: Theme.of(context).primaryColor,
            ),
          ),
          AnimatedOpacity(
            duration: const Duration(milliseconds: 150),
            opacity: 1 - progress,
            child: CustomPaint(
              painter: CustomToolbarShape(
                showBar: false,
              ),
            ),
          ),
          AnimatedContainer(
            duration: const Duration(milliseconds: 100),
            padding: EdgeInsets.lerp(
              EdgeInsets.symmetric(horizontal: 10),
              EdgeInsets.symmetric(horizontal: 10),
              progress,
            ),
            alignment: Alignment.lerp(
              Alignment.center,
              Alignment.topLeft,
              progress,
            ),
            child: Image.asset(
              'assets/image/logo_white.png',
              fit: BoxFit.cover,
              height: 180,
            ),
          ),
          AppBar(
            backgroundColor: Colors.transparent,
            toolbarHeight: 70,
            // leading: BackButton(),
            actions: [
              IconButton(
                icon: const Icon(
                  Icons.star,
                  size: 28,
                ),
                onPressed: () {
                  if (Provider.of<AuthProvider>(context, listen: false)
                      .isLoggedIn()) {
                    Navigator.of(context).push(MaterialPageRoute(
                        builder: (_) => FavouriteClassifiedScreen()));
                  } else {
                    Navigator.of(context)
                        .push(MaterialPageRoute(builder: (_) => LoginScreen()));
                  }
                },
              ),
              IconButton(
                icon: Icon(
                  Icons.search,
                  size: 28,
                ),
                onPressed: () {
                  Navigator.of(context).push(MaterialPageRoute(
                      builder: (_) => ClassifiedFilterScreen()));
                  // setState(() {
                  //   _search = !_search;
                  // });
                },
              ),
              InkWell(
                borderRadius: BorderRadius.circular(5),
                onTap: () {
                  if (Provider.of<AuthProvider>(context, listen: false)
                      .isLoggedIn()) {
                    Navigator.of(context).push(MaterialPageRoute(
                        builder: (_) => CreateClassifiedScreen()));
                  } else {
                    Navigator.of(context)
                        .push(MaterialPageRoute(builder: (_) => LoginScreen()));
                  }
                },
                child: Padding(
                  padding: EdgeInsets.symmetric(horizontal: 10),
                  child: Center(
                    child: Row(
                      children: [
                        Text(
                          'Hızlı İlan',
                          style: TextStyle(
                            fontSize: 15,
                            fontWeight: FontWeight.w500,
                          ),
                        ),
                        SizedBox(
                          width: 8,
                        ),
                        Icon(
                          Icons.add_circle_outline,
                          size: 28,
                          color: Colors.white,
                        ),
                      ],
                    ),
                  ),
                ),
              ),
            ],
            elevation: 0,
          ),
        ],
      ),
    );
  }

  @override
  double get maxExtent => 300;

  @override
  double get minExtent => 70;

  @override
  bool shouldRebuild(covariant SliverPersistentHeaderDelegate oldDelegate) =>
      true;
}
